property.code:
var PhenoTips = (function (PhenoTips) {
  var widgets = PhenoTips.widgets = PhenoTips.widgets || {};
  widgets.MatchingNotificationManager = Class.create({

    initialize : function (patientId) {
      var _this = this;

      //key: name in JSON, value: name for presentation
      this.columns = {'id'               : 'id',
                      'patientId'        : 'patient',
                      'matchedPatientId' : 'matched patient',
                      'score'            : 'score',
                      'genotypicScore'   : 'gscore',
                      'phenotypicScore'  : 'pscore',
                      'notified'         : 'notified',
                      'outgoingRequest'  : 'outgoing',
                      'remoteId'         : 'remote',
                      'timestamp'        : 'timestamp',
                      'genes'            : 'genes',
                      'matchedGenes'     : 'matched genes'};
//                      'email'            : 'email',
//                      'matchedHref'      : 'matched HREF'};

      this.matchesTable = $('matchesTable').select('tbody')[0];
      this.ajaxURL = new XWiki.Document('WebHome', 'MatchingNotification').getURL('get') + '?outputSyntax=plain';
      this.matches = [];

      this._initializeTable();

      $('find-matches-button').observe('click', function() {
        var score = _this._checkScore('find-matches-score', 'find-matches-messages');
        if (score == undefined) {
          return;
        }
        new Ajax.Request(_this.ajaxURL,
          {  parameters : {action : 'find-matches',
                           score  : score},
             onSuccess : function (response) {
                _this._showSuccess('find-matches-messages');
             },
             onFailure : function (response) {
                _this._showFailure('find-matches-messages');
             }
          }
        );
        _this._showSent('find-matches-messages');
      });

      $('show-matches-button').observe('click', function() {
        _this._showMatches();
      });

      $('send-notifications-button').observe('click', function() {
        var ids = _this._readMatchesToNotify(matchesTable);
        var idsJson = JSON.stringify({ ids: ids});
        console.log("Sending " + idsJson);
        new Ajax.Request(_this.ajaxURL,
          {   parameters : {action : 'send-notifications',
                            idsForNotification : idsJson
                           },
              onSuccess : function (response) {
                _this._showSuccess('send-notifications-messages', response.responseText);
                _this._updateAfterNotification(response.responseJSON.results);
              },
              onFailure : function (response) {
                _this._showFailure('send-notifications-messages');
              }
          }
        );
        _this._showSent('send-notifications-messages');
      });

      $('checkbox_all').observe('click', function(event) {
        var checked = event.target.checked;
        $$("#matchesTable input[type=checkbox]").each(function (elm) {
          if (!elm.disabled) {
            elm.checked = checked;
          }
        });
      });

      $('checkbox_filter_notified').observe('click', function(event) {
        _this._buildTable({'filterNotified' : event.target.checked});
      });
    },

    _showMatches : function(params)
    {
      var _this = this;
      var score = _this._checkScore('show-matches-score', 'show-matches-messages');
      if (score == undefined) {
        return;
      }
      new Ajax.Request(this.ajaxURL,
        {  parameters : {action : 'show-matches',
                         score  : score},
           onSuccess : function (response) {
              _this._showSuccess('show-matches-messages');
              console.log(response.responseJSON);
              _this.matches = response.responseJSON.matches;
              _this._buildTable(params);
           },
           onFailure : function (response) {
              _this._showFailure('show-matches-messages');
           }
        }
      );
      _this._showSent('show-matches-messages');
    },

    _checkScore : function(scoreFieldName, messagesFieldName) {
      var score = $(scoreFieldName).value;
      var messages = $(messagesFieldName);
      if (score == undefined || score == "") {
         messages.update(new Element('div', {'class' : 'xHint'}).update("$services.localization.render('phenotips.matchingNotifications.emptyScore')"));
         return;
      } else if (isNaN(score)) {
         messages.update(new Element('div', {'class' : 'xHint'}).update("$services.localization.render('phenotips.matchingNotifications.invalidScore')"));
         return;
      };
      scoreNumber = Number(score);
      if (scoreNumber <0 || scoreNumber >1) {
         messages.update(new Element('div', {'class' : 'xHint'}).update("$services.localization.render('phenotips.matchingNotifications.invalidScore')"));
         return;
      }
      messages.update('');
      return score;
    },

    _showSent : function(messagesFieldName) {
      var messages = $(messagesFieldName);
      messages.update(new Element('div', {'class' : 'xHint'}).update("$services.localization.render('phenotips.matchingNotifications.requestSent')"));
    },

    _showSuccess : function(messagesFieldName, value) {
      var messages = $(messagesFieldName);
      if (value == undefined) {
        value = "$services.localization.render('phenotips.matchingNotifications.success')";
      }
      messages.update(new Element('div', {'class' : 'xHint'}).update(value));
    },

    _showFailure : function(messagesFieldName) {
      var messages = $(messagesFieldName);
      messages.update(new Element('div', {'class' : 'xHint'}).update("$services.localization.render('phenotips.matchingNotifications.failure')"));
    },

    _initializeTable : function() {
      var header = $('matchesTable').select('thead')[0];

      var headerTr = new Element('tr');
      header.insert(headerTr);

      headerTr.insert(new Element('th'));

      for (var column in this.columns) {
        var e = new Element('th').update(this.columns[column]);
        headerTr.insert(e);
      }
    },

    _buildTable : function(params)
    {
      params = params || {};

      this.matchesTable.update('');

      for (var i = 0 ; i < this.matches.length ; i++) {
          var row = new Element('tr', {'class':'ok'});
          var currentMatch = this.matches[i];
          if (currentMatch.notified && params.filterNotified) {
            continue;
          }

          //checkbox
          var notified = currentMatch.notified;
          var checkboxId = 'checkbox_' + currentMatch.id;
          var e = new Element('input', {'type':'checkbox', 'id':checkboxId , 'disabled':notified, 'checked':notified});
          row.insert(e);

          for (var column in this.columns) {
              var cellValue = currentMatch [column];

              if (cellValue == null) { cellValue = ""; }

              var e = new Element('td').update(cellValue);
              row.insert(e);
          }
          this.matchesTable.insert(row);
      }

      params.results.each(function(item) {
        var id = item.id;
        var success = item.success;
        var checkbox = $('checkbox_' + id);
        if (checkbox != undefined && success != undefined) {
           var row = checkbox.up();
           row.setAttribute('class', success ? 'successful' : 'failed');
         }
      });

    },

    _readMatchesToNotify : function()
    {
      ids = [];
      $$("#matchesTable input[type=checkbox]").each(function (elm) {
           if (elm.checked && elm && !elm.disabled) {
             id = Number(elm.id.substr(elm.id.indexOf('_')+1))
             ids.push(String(id));
           }
        });
      return ids;
    },

    _updateAfterNotification : function(results)
    {
      this._showMatches({'results' : results});
    }
  });
  return PhenoTips;
}(PhenoTips || {}));


var matchingNotificationInit = function() {
  new PhenoTips.widgets.MatchingNotificationManager();
};
(XWiki.domIsLoaded && matchingNotificationInit()) || document.observe("xwiki:dom:loaded", matchingNotificationInit);

.successful {
  color: green;
}

.failed {
  color: red;
}

.ok {
  color: inherit;
}
